        bool valid = true;
        int now = 0;
        while (!(i == na.size() && j == b.size())) {
            if (i == na.size()) now += n + 1 - b[j++].second;
            else if (j == b.size()) now += n + 1 - na[i++].second;
            else {
                valid &= !na[i].first || na[i].first == b[j].first;
                now += abs(na[i++].second - b[j++].second);
            }
