int main() {
	int n; string s, t;
	cin >> n >> s >> t;
	vector<int> sps = { 0 }, spt = { 0 };
	for (int i = 1; i < n; ++i) {
		if (s[i - 1] != s[i]) {
			sps.push_back(i);
		}
		if (t[i - 1] != t[i]) {
			spt.push_back(i);
		}
	}
	sps.push_back(n);
	spt.push_back(n);
	int parity = (s[0] == t[0] ? 0 : 1);
	int ls = sps.size(), lt = spt.size(), ans = (1 << 30);
	for (int i = -ls + 1; i < lt; ++i) {
		if (abs(i) % 2 != parity) continue;
		int sum = 0;
		for (int j = -lt + 1; j < ls + lt; ++j) {
			int ps = j - i;
			if (ps < 0) ps = 0;
			else if (ps >= ls) ps = n;
			else ps = sps[ps];
			int pt = j;
			if (pt < 0) pt = 0;
			else if (pt >= lt) pt = n;
			else pt = spt[pt];
			sum += abs(ps - pt);
		}
		ans = min(ans, sum);
	}
	cout << ans << endl;
	return 0;
