int make(int x)
{
        assert(x <= n);
        int res = 1, r = 0, l = 0;
        for(int i = x + 1; a[i] != a[x] && i <= n; i++){
                r += 1;
        }
        for(int i = x - 1; a[i] != a[x] && i >= 1; i--){
                l += 1;
        }
        if(l + r < 2){
                a[x] ^= 1;
                return res;
        }
        res += make(x + 2 - l);
        a[x] ^= 1;
        return res;
}

