#include <bits/stdc++.h>
using namespace std;

const int MAXN = 5e3 + 5;
const int INF = 2e9;

struct str {
	int first;
	vector <int> v;
	vector <int> pos[3];

	void input(int n) {
		char buff[MAXN];
		scanf("%s", buff);
		first = buff[0] - '0';
		int cnt = 0;
		for (int i = 0; i <= n; i++) {
			if (i == n || (i && buff[i] != buff[i - 1])) {
				pos[cnt].push_back(v.size());
				v.push_back(cnt);
				cnt = 0;
			}
			cnt++;
		}
	}

	int get(int num, int id) const {
		if (id >= 0 && id < pos[num].size())
			return pos[num][id];
		if (id == -1)
			return -1;
		if (id == pos[num].size())
			return v.size();
		return INF;
	}

	int calc(int k, int l, const str &other) const {
		int diff = (int)other.v.size() - (int)v.size();
		int res = INF;
		for (int lft = first ^ other.first; lft < v.size(); lft += 2) {
			int rig = (diff - lft * (2 * k - 1)) * (2 * l - 1);
			int L = k ? get(2, lft - 1) : get(1, 2 * lft - 1);
			int R = l ? get(2, (int)pos[2].size() - rig) : get(1, (int)pos[1].size() - 2 * rig);
			if (L == INF || R == INF || L >= R)
				continue;

			int curr = lft + rig;
			vector <int> pref, suff;

			if (k) {
				for (int i = 0; i < lft; i++)
					curr += pos[2][i] + i;
				pref = vector <int> (L + 1 + lft, 1);
			}
			else {
				for (int i = 0; i < 2 * lft; i++)
					curr += pos[1][i] - i;
				pref = vector <int> (L + 1 - lft, 2);
			}

			if (l) {
				for (int i = 0; i < rig; i++)
					curr += (int)v.size() - pos[2][(int)pos[2].size() - i - 1] - 1 + i;
				suff = vector <int> ((int)v.size() - R + rig, 1);
			}
			else {
				for (int i = 0; i < 2 * rig; i++)
					curr += (int)v.size() - pos[1][(int)pos[1].size() - i - 1] - 1 - i;
				suff = vector <int> ((int)v.size() - R - rig, 2);
			}

			for (int i = L + 1; i < R; i++)
				pref.push_back(v[i]);

			for (auto it : suff)
				pref.push_back(it);

			int ptr = 0;
			for (int i = 0; i < pref.size(); i++)
				if (pref[i] == 1)
					curr += abs(other.pos[1][ptr++] - i);

			assert(pref.size() == other.v.size());
			assert(ptr == other.pos[1].size());

			res = min(res, curr); 
		}

		return res;
	}

	int dist(const str &other) const {
		int res = INF;
		for (int i = 0; i < 2; i++)
			for (int j = 0; j < 2; j++)
				res = min(res, calc(i, j, other));
		return res;
	}
};

int N;
str S, T;

int main() {
	scanf("%d", &N);
	S.input(N);
	T.input(N);
	printf("%d\n", S.dist(T));
	return 0;
}

