inline void solve()
{
	static int a[MAXN + 5], b[MAXN + 5];

	int cnta[2] = {0, 0}, cntb[2] = {0, 0};
	for(int i = 1; i < n; ++i)
	{
		if(s[i] != s[i + 1]) ++cnta[a[i] = s[i] == '0' ? 0 : 1]; else a[i] = -1;
		if(t[i] != t[i + 1]) ++cntb[b[i] = t[i] == '0' ? 0 : 1]; else b[i] = -1;
	}

	auto calc = [cnta, cntb] (int d)
	{
		if(cnta[d] >= cntb[d])
		{
			int K = cnta[d] - cntb[d];
			int ans = INT_MAX;
			for(int i = 0; i <= K; ++i)
			{
				static vector<int> A, B;
				A.clear();
				B.clear();

				for(int j = 1; j < n; ++j) if(a[j] == d) A.push_back(j);

				for(int j = 1; j <= i; ++j) B.push_back(0);
				for(int j = 1; j < n; ++j) if(b[j] == d) B.push_back(j);
				for(int j = 1; j <= K - i; ++j) B.push_back(n);

				int res = 0;
				for(int j = 0; j < SZ(A); ++j) res += abs(A[j] - B[j]);
				chkmin(ans, res);
			}
			return ans;
		}
		else
		{
			int K = cntb[d] - cnta[d];
			int ans = INT_MAX;
			for(int i = 0; i <= K; ++i)
			{
				static vector<int> A, B;
				A.clear();
				B.clear();

				for(int j = 1; j <= i; ++j) A.push_back(0);
				for(int j = 1; j < n; ++j) if(a[j] == d) A.push_back(j);
				for(int j = 1; j <= K - i; ++j) A.push_back(n);

				for(int j = 1; j < n; ++j) if(b[j] == d) B.push_back(j);

				int res = 0;
				for(int j = 0; j < SZ(A); ++j) res += abs(A[j] - B[j]);
				chkmin(ans, res);
			}
			return ans;
		}
	};

	printf("%d\n", calc(0) + calc(1));
}

