#include <iostream>

const int N = 5000 + 7;

int n;
int a[N], b[N];
int xa[3], xb[3];
int ans;

int abs(int x) {
	return x < 0 ? -x : x;
}
void calc(int x, int y) {
	if (!x || !y) return;
	int cur = 0;
	for (int i = x, j = y; j <= n; ++j) {
		if (b[j] == -1) continue;
		for (; i < n && a[i] == -1; ++i);
		cur += abs(i - j);
		if (i < n) ++i;
	}
	for (int i = x - 1, j = y - 1; j; --j) {
		if (b[j] == -1) continue;
		for (; i && a[i] == -1; --i);
		cur += abs(i - j);
		if (i) --i;
	}
	ans = std::min(ans, cur);
}

