	for (int i = 1; i < n; ++i)
		a[i] = a[i] == a[i + 1] ? -1 : a[i],
		b[i] = b[i] == b[i + 1] ? -1 : b[i];
	a[n] = b[n] = -1;
	for (int i = n; i; --i) {
		if (a[i] != -1) xa[a[i]] = i;
		if (b[i] != -1) xb[b[i]] = i;
	}
	for (int i = 1; i <= n; ++i)
		ans += abs(a[i] - b[i]);
