inline bool ckl(R int j){return !(j>2&&(a[j-1]^a[j])&&(a[j-2]^a[j]));}
inline bool ckr(R int j){return !(j<n-1&&(a[j+1]^a[j])&&(a[j+2]^a[j]));}
inline bool ckm(R int j){return !(j>1&&j<n&&(a[j-1]^a[j])&&(a[j+1]^a[j]));}
inline bool ck(R int j){return ckl(j)&&ckr(j)&&ckm(j);}
