		if(!ck(i)){
			if(!ckm(i)&&!ckl(i))a[i-1]^=1,++res;
			else if(ckm(i)&&!ckl(i)){
				for(R int j=i-1;j>=0;j-=2){
					if(j<=1||a[j-1]!=a[j]){
						for(R int k=j+1;k<i;k+=2)a[k]^=1,++res;
						break;
					}
				}
			}
			else{
				for(R int j=i-1;j;--j){
					if(j==1||a[j-1]==a[j]){
						for(R int k=j;k<i;++k)a[k]^=1,++res;
						break;
					}
				}
			}
		}
		a[i]^=1,++res;
	}
	fp(i,1,n)a[i]=s[i]-'0',b[i]=t[i]-'0';
