				for(R int j=i+1;j<=n+1;j+=2){
					if(j>=n||a[j+1]!=a[j]){
						for(R int k=j-1;k>i;k-=2)a[k]^=1,++cnt;
						break;
					}
				}
			}
			else{
				for(R int j=i+1;j<=n;++j){
					if(j==n||a[j+1]==a[j]){
						for(R int k=j;k>i;--k)a[k]^=1,++cnt;
						break;
					}
				}
			}
		}
		a[i]^=1,++cnt;
	}
	printf("%d\n",min(res,cnt));
	return 0;
}

