using ll = long long;
template<class T> using V = vector<T>;
template<class T> using VV = vector<vector<T>>;
int solve(bool dif0,V<int> x,V<int> y){
	int N = x.size();
	if(N==0) return dif0?1:0;
	int x0 = 0;
	int y0 = 0;
	V<int> x0s,y0s;
	rep(i,N) if(x[i]==0) x0++,x0s.pb(i);
	rep(i,N) if(y[i]==0) y0++,y0s.pb(i);
	int ans = 1e9;
	for(int l = -x0-1; l<=y0+1; l++){
		int r = y0-x0-l;
		if( (l%2!=0) ^ dif0 ) continue;
		int cost = 0;
		if(l>0){
			rep(i,l) cost += i==y0 ? N+1 : y0s[i]+1;
		}else{
			rep(i,-l) cost += i==x0 ? N+1 : x0s[i]+1;
		}
		if(r>0){
			rep(i,r) cost += i==y0 ? 0 : N-1-y0s[y0-1-i]+1;
		}else{
			rep(i,-r) cost += i==x0 ? 0 : N-1-x0s[x0-1-i]+1;
		}
		chmin(ans,cost);
	}
	return ans;
}
int main(){
	int N;
	string s,t;
	cin>>N>>s>>t;
	bool dif0 = s[0]!=t[0];
	V<int> x(N-1),y(N+1);
	rep(i,N-1){
		x[i] = s[i]!=s[i+1];
		y[i] = t[i]!=t[i+1];
	}
	cout<<solve(dif0,x,y)<<endl;

}

