using ll = long long;
template<class T> using V = vector<T>;
template<class T> using VV = vector<vector<T>>;
int solve(bool dif0,V<int> x,V<int> y){
	int N = x.size();
	if(N==0) return dif0?1:0;
	int x0 = 0;
	int y0 = 0;
	V<int> x0s,y0s;
	rep(i,N) if(x[i]==0) x0++,x0s.pb(i);
	rep(i,N) if(y[i]==0) y0++,y0s.pb(i);
	int ans = 1e9;
	for(int l = -x0-1; l<=y0+1; l++){
		int r = y0-x0-l;
		if( (l%2!=0) ^ dif0 ) continue;
		V<int> a,b;
		if(l>0){
			rep(i,l) a.pb(-1);
		}else{
			rep(i,-l) b.pb(-1);
		}
		for(int v:x0s) a.pb(v);
		for(int v:y0s) b.pb(v);
		if(r>0){
			rep(i,r) a.pb(N);
		}else{
			rep(i,-r) b.pb(N);
		}
		assert(a.size() == b.size());
		int cost = 0;
		int K = a.size();
		rep(i,K) cost += abs(a[i]-b[i]);
		chmin(ans,cost);
	}
	return ans;
}
