int N;
string S,T;

vector<int> A,B;

void solve() {
	int i,j,k,l,r,x,y; string s;

	cin>>N>>S>>T;
	S="0"+S;
	T="0"+T;

	for(i=1;i<=N;i++) {
		if(S[i]!=S[i-1]) A.push_back(i-1);
		if(T[i]!=T[i-1]) B.push_back(i-1);
	}

	if(A.size()<B.size()) swap(A,B);

	int mi=1<<30;
	for(int D=-(N+2);D<=N+2;D++) if(D%2==0) {
		int tot=0;
		FOR(i,A.size()) {
			y=i+D;
			if(y<0) r=0;
			else if(y>=B.size()) r=N;
			else r=B[y];
			tot+=abs(A[i]-r);
		}
		FOR(i,B.size()) {
			y=i-D;
			if(y>=0 && y<A.size()) continue;
			if(y<0) r=0;
			else r=N;
			tot+=abs(B[i]-r);
		}

		mi=min(mi,tot);
	}

	cout<<mi<<endl;

}


