const int N = 5010, INF = 0x3f3f3f3f;
char s[N], t[N];
int n,ans,pla[N << 1],cnt,rec[N << 1];
int solve(int p) {
  int tmp = 0;
  rep (i, 1, p) pla[++tmp] = 0;
  int ret = 0;
  if (p >= 0) pla[++tmp] = 0;
  else ++ p;
  for (int i = 1; i < n; ++ i) {
    if (s[i] != s[i+1]) {
      if (p < 0) ++ p, ret += i;
      else pla[++tmp] = i;
    }
  }
  while (tmp < cnt) pla[++tmp] = n;
  rep (i, 1, tmp) {
    if (i <= cnt) ret += abs(pla[i] - rec[i]);
    else ret += abs(pla[i] - n);
  }
  return ret;
}
