		// for(int j=1;j<=m;++j) {
		// 	printf("%c", g[j]<=1 ? g[j]+'0' : '|');
		// } puts("");
		while(L<=m && R<=m) {
			if(f[L]==2 && g[R]==2) {
				res+=abs(L-R);
				++L;
				++R;
			}
			while(L<=m && f[L]!=2) ++L;
			while(R<=m && g[R]!=2) ++R;
		}
		// printf("==> %d\n", res);
		ans=min(ans, res);
	}
	printf("%d\n", ans);
	return 0;
