		while (s[0].size() > s[1].size()) {
			s[1].eb(mp(-1, N-1));
		}

		bool ok = true;
		ll dis = 0;

		rep(i, s[0].size()) {
			if (s[0][i].fi != -1 && s[1][i].fi != -1 && s[0][i].fi != s[1][i].fi) {
				ok = false;
			}
			dis += abs(s[0][i].se - s[1][i].se);
		}

		if (ok) {
			ans = min(ans, dis);
		}
	}

	cout << ans << endl;

	return 0;
}

