int n;

int solve(vector <int> a, vector <int> b) {
  if(a.size() < b.size()) swap(a, b);
  int ans = 2e9;
  for(int bef = 0; bef <= a.size() - b.size(); ++ bef) {
    int res = 0;
    for(int j = 0; j < bef; ++ j)
      res += a[j];
    for(int j = bef; j < bef + b.size(); ++ j)
      res += abs(a[j] - b[j - bef]);
    for(int j = bef + b.size(); j < a.size(); ++ j)
      res += abs(n - a[j]);
    ans = min(ans, res);
  }
  return ans;
}

