	for (int i = 1; i <= n; i++) {
		if (s[i] != s[i+1]) {
			vec1.push_back(pi((s[i] - '0')^1, i));
		}
	}
	for (int i = 1; i <= n; i++) vec1.push_back(pi(vec1[vec1.size()-1].first^1, n));
	vec2.push_back(pi(t[1] - '0', 0));
	for (int i = 1; i <= n; i++) {
		if (t[i] != t[i+1]) {
			vec2.push_back(pi((t[i] - '0')^1, i));
		}
	}
	int ans = 0x3f3f3f3f;
	int sz = int (vec2.size());
	for (int i = 0; i+sz-1 < vec1.size(); i++) {
		if (vec1[i].first != vec2[0].first) continue;
		int res = 0;
		for (int j = 0; j < sz; j++) res += abs(vec1[i+j].second - vec2[j].second);
		for (int j = 0; j < i; j++) res += abs(vec1[j].second - 0);
		for (int j = i+sz; j < vec1.size(); j++) res += abs(vec1[j].second - n);
		if (res < ans) ans = res;
	}
	printf("%d\n", ans);
	return 0;
}

