int _abs(int x) {
	return x < 0 ? -x : x;
}

int solve(int p, int q) {
	int ret = 0;
	for (int i = 0; i + p <= k || i + q <= l; i++) {
		int x = i + p <= k ? a[i + p] : n;
		int y = i + q <= l ? b[i + q] : n;
		ret += _abs(x - y);
	}
	for (int i = 1; p - i >= 1 || q - i >= 1; i++) {
		int x = p - i >= 1 ? a[p - i] : 0;
		int y = q - i >= 1 ? b[q - i] : 0;
		ret += _abs(x - y);
	}
	return ret;
}

