sll so (sll a[], sll n) {
	if (n == 1) return 0;
	sll l = n / 2, r = n - l;
	sll inv = so(a, l) + so(a + l, r);

	sll i = 0, j = 0;

	sll tmp[n];
	while (i < l || j < r) {
		bool isr;
		if (i == l) {
			isr = true;
		} else if (j == r) {
			isr = false;
		} else if (a[i] > a[l + j]) {
			isr = true;
		} else {
			isr = false;
		}

		if (isr) {
			inv += (l - i);
			tmp[i + j] = a[l + j];
			j++;
		} else {
			tmp[i + j] = a[i];
			i++;
		}
	}
	for (i = 0; i < n; i++) {
		a[i] = tmp[i];
	}
	return inv;
}

