public class Main {

	void submit() {
		int n = nextInt();
		String s = nextToken();
		String t = nextToken();

		if (n < 3) {
			int ret = 0;
			for (int i = 0; i < n; i++) {
				ret += Math.abs(s.charAt(i) - t.charAt(i));
			}
			out.println(ret);
			return;
		}

		ArrayList<Integer> a = new ArrayList<>();
		ArrayList<Integer> b = new ArrayList<>();

		if (s.charAt(0) != t.charAt(0)) {
			b.add(0);
		}

		for (int i = 1; i < n; i++) {
			if (s.charAt(i - 1) != s.charAt(i)) {
				a.add(i);
			}
			if (t.charAt(i - 1) != t.charAt(i)) {
				b.add(i);
			}
		}

		int ret = Integer.MAX_VALUE;

		for (int shift = -2 * n; shift <= 2 * n; shift += 2) {
			int cur = 0;
			for (int i = 0; i < a.size(); i++) {
				cur += Math.abs(a.get(i) - get(b, i + shift, n));
			}
			for (int i = 0; i < b.size(); i++) {
				if (i - shift < 0 || i - shift >= a.size()) {
					cur += Math.abs(b.get(i) - get(a, i - shift, n));
				}
			}
			ret = Math.min(ret, cur);
		}

		out.println(ret);
	}

	int get(ArrayList<Integer> a, int idx, int n) {
		if (idx < 0) {
			return 0;
		} else if (idx >= a.size()) {
			return n;
		} else {
			return a.get(idx);
		}
	}

	void test() {

	}

	void stress() {
		for (int tst = 0;; tst++) {
			if (false) {
				throw new AssertionError();
			}
			System.err.println(tst);
		}
	}

	Main() throws IOException {
		is = System.in;
		out = new PrintWriter(System.out);
		submit();
		// stress();
		// test();
		out.close();
	}

	static final Random rng = new Random();
	static final int C = 5;

	static int rand(int l, int r) {
		return l + rng.nextInt(r - l + 1);
	}

	public static void main(String[] args) throws IOException {
		new Main();
	}

	private InputStream is;
	PrintWriter out;

	private byte[] buf = new byte[1 << 14];
	private int bufSz = 0, bufPtr = 0;

	private int readByte() {
		if (bufSz == -1)
			throw new RuntimeException("Reading past EOF");
		if (bufPtr >= bufSz) {
			bufPtr = 0;
			try {
				bufSz = is.read(buf);
			} catch (IOException e) {
				throw new RuntimeException(e);
			}
			if (bufSz <= 0)
				return -1;
		}
		return buf[bufPtr++];
	}

	private boolean isTrash(int c) {
		return c < 33 || c > 126;
	}

	private int skip() {
		int b;
		while ((b = readByte()) != -1 && isTrash(b))
			;
		return b;
	}

	String nextToken() {
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while (!isTrash(b)) {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	String nextString() {
		int b = readByte();
		StringBuilder sb = new StringBuilder();
		while (!isTrash(b) || b == ' ') {
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	double nextDouble() {
		return Double.parseDouble(nextToken());
	}

	char nextChar() {
		return (char) skip();
	}

	int nextInt() {
		int ret = 0;
		int b = skip();
		if (b != '-' && (b < '0' || b > '9')) {
			throw new InputMismatchException();
		}
		boolean neg = false;
		if (b == '-') {
			neg = true;
			b = readByte();
		}
		while (true) {
			if (b >= '0' && b <= '9') {
				ret = ret * 10 + (b - '0');
			} else {
				if (b != -1 && !isTrash(b)) {
					throw new InputMismatchException();
				}
				return neg ? -ret : ret;
			}
			b = readByte();
		}
	}

	long nextLong() {
		long ret = 0;
		int b = skip();
		if (b != '-' && (b < '0' || b > '9')) {
			throw new InputMismatchException();
		}
		boolean neg = false;
		if (b == '-') {
			neg = true;
			b = readByte();
		}
		while (true) {
			if (b >= '0' && b <= '9') {
				ret = ret * 10 + (b - '0');
			} else {
				if (b != -1 && !isTrash(b)) {
					throw new InputMismatchException();
				}
				return neg ? -ret : ret;
			}
			b = readByte();
		}
	}
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.StringTokenizer;
import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskE solver = new TaskE();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskE {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int n = in.nextInt();
            String s = in.next();
            String t = in.next();
            TaskE.Sequence seq1 = parse(s);
            TaskE.Sequence seq2 = parse(t);
            int parity = (s.charAt(0) == t.charAt(0) ? 0 : 1);
            long res = Long.MAX_VALUE;
            for (int leftExplosions = -seq2.twos.length - 1; leftExplosions <= seq1.twos.length + 1; ++leftExplosions) {
                if ((leftExplosions + parity) % 2 != 0) continue;
                int rightExplosions = seq1.twos.length - seq2.twos.length - leftExplosions;
                long cur = 0;
                int remains = seq1.twos.length;
                int offset1 = 0;
                int offset2 = 0;
                int delta = 0;
                if (leftExplosions > 0) {
                    remains -= leftExplosions;
                    offset1 += leftExplosions;
                    delta += leftExplosions;
                    for (int i = 0; i < leftExplosions; ++i) {
                        if (i < seq1.twos.length) {
                            cur += seq1.twos[i] + (i + 1);
                        } else {
                            cur += 1 + (n - 2);
                        }
                    }
                } else if (leftExplosions < 0) {
                    offset2 += -leftExplosions;
                    delta -= -leftExplosions;
                    for (int i = 0; i < -leftExplosions; ++i) {
                        if (i < seq2.twos.length) {
                            cur += seq2.twos[i] + (i + 1);
                        } else {
                            cur += 1 + (n - 2);
                        }
                    }
                }
                if (rightExplosions > 0) {
                    remains -= rightExplosions;
                    for (int i = 0; i < rightExplosions; ++i) {
                        if (i < seq1.twos.length) {
                            cur += (seq1.len - 1 - seq1.twos[seq1.twos.length - 1 - i]) + (i + 1);
                        } else {
                            cur += 1;
                        }
                    }
                } else if (rightExplosions < 0) {
                    for (int i = 0; i < -rightExplosions; ++i) {
                        if (i < seq2.twos.length) {
                            cur += (seq2.len - 1 - seq2.twos[seq2.twos.length - 1 - i]) + (i + 1);
                        } else {
                            cur += 1;
                        }
                    }
                }
                if (remains > 0) {
                    for (int i = 0; i < remains; ++i) {
                        cur += Math.abs(seq2.twos[offset2 + i] - seq1.twos[offset1 + i] + delta);
                    }
                }
                res = Math.min(res, cur);
            }
            out.println(res);
        }

        private TaskE.Sequence parse(String s) {
            int[] res = new int[s.length()];
            int ptr = 0;
            int pos = 0;
            int start = 0;
            for (int i = 1; i <= s.length(); ++i) {
                if (i == s.length() || s.charAt(i) != s.charAt(i - 1)) {
                    if (i - start == 2) res[ptr++] = pos;
                    ++pos;
                    start = i;
                }
            }
            return new TaskE.Sequence(Arrays.copyOf(res, ptr), pos);
        }

        static class Sequence {
            int[] twos;
            int len;

            public Sequence(int[] twos, int len) {
                this.twos = twos;
                this.len = len;
            }

        }

    }

    static class InputReader {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

