int main()
{
	while(true){
		string S;
		getline(cin, S);
		istringstream ist(S);

		int m;
		ist >> m;

		if(m==0)
			break;

		int N[40], P[40];
		for(int i=0; i<m; i++){
			getline(cin, S);
			istringstream ist(S);

			ist >> N[i];

			int p[21];
			for(int j=0; j<21; j++){
				ist >> p[j];

				if(ist.eof())
					break;
			}

			P[i] = 0;
			int k = 0;
			for(int j=0; j<9; j++){
				P[i] += p[k];
				if(p[k]==10){
					P[i] += (p[k+1]+p[k+2]);
					k++;
					continue;
				}

				P[i] += p[k+1];
				if(p[k]+p[k+1]==10){
					P[i] += p[k+2];
				}
				k += 2;
			}

			P[i] += (p[k]+p[k+1]);
			if(p[k]==10||p[k]+p[k+1]==10){
				P[i] += p[k+2];
			}
		}

		for(int i=0; i<m-1; i++){
			for(int j=i+1; j<m; j++){
				if(P[i]<P[j]||(P[i]==P[j]&&N[i]>N[j])){
					int tmp = N[i];
					N[i] = N[j];
					N[j] = tmp;

					tmp = P[i];
					P[i] = P[j];
					P[j] = tmp;
				}
			}
		}

		for(int i=0; i<m; i++){
			cout << N[i] << ' ' << P[i] << '\n';
		}
	}

	return 0;
}

