int solve(int *f, int n){
  int res = 0;
  //  printf("%d ", *f);
  if(*f == 1 || *f == 2){ res = n*2; (*f)--;}
  else if(*f == 3){ res = n*3; *f = 1;}
  else res = n;
  //  printf("%d\n", *f);
  return res;
}

