			for(int j=0;j<9;j++){
				if(sc[j][0]==10){
					sck[j]=10;
					if(sc[j+1][0]==10)sck[j]+=10+sc[j+2][0];
					else sck[j]+=sc[j+1][0]+sc[j+1][1];
				}else if(sc[j][0]+sc[j][1]==10){
					sck[j]=10+sc[j+1][0];
				}else sck[j]=sc[j][0]+sc[j][1];
			}
			for(int j=0;j<=9;j++)res+=sck[j];
			s.insert(make_pair(res,num));
