II solve( int no, VI A ) {
    VI F(10), S(10), C(10);
    VI T(10, 0);
    int t = 0;
    for ( int i = 0; i < 10; ++ i ) {
        F[i] = 0, S[i] = 0, C[i] = 0;
        if ( A[t] == 10 ) {
            F[i] = A[t++];
        } else {
            F[i] = A[t++];
            S[i] = A[t++];
        }
        if ( i == 9 ) {
            if ( F[i] == 10 ) {
                S[i] = A[t++];
            }
            C[i] = 0;
            if ( F[i] + S[i] >= 10 ) C[i] = A[t++];
        } else {
        }
    }

    for ( int i = 0; i < 10; ++ i ) {
        T[i] = F[i] + S[i] + C[i];
    }

    int addScore = 0;
    for ( int i = 0; i < 10; ++ i ) {
        if ( i < 9 ) {
            if ( F[i] == 10 ) {
                if ( i < 8 ) {
                    if ( F[i+1] == 10 ) {
                        addScore += 10 + F[i+2];
                    } else {
                        addScore += F[i+1] + S[i+1];
                    }
                } else if ( i == 8 ) {
                    addScore += F[9] + S[9];
                }
            } else if ( F[i] + S[i] >= 10 ) {
                addScore += F[i+1];
            }
        }
    }
    return II( accumulate( T.begin(), T.end(), 0 ) + addScore, no );
}

