//string型からint型に変換する
int s_to_i(string s){
    int n = 0;
    for(unsigned int i=0 ; i < s.size() ; ++i ){
        n *= 10;
        n += s[i] - '0';
    }
    return n;
}

// 文字列を半角スペース区切りで数値に分割する
vector<int> split_words(string s){
    bool flag = false;
    vector<string> vs;
    string str;
    s.push_back(' ');
    for(int i=0 ; i < s.size() ; i++ ){
        char c = s[i];
        if( flag == false ){
            if( c != ' ' ){
                flag = true;
                str.clear();
                str.push_back( c );
            }
        }else{
            if( c != ' ' ){
                str.push_back( c );
            }
            if( c == ' ' ){
                flag = false;
                vs.push_back( str );
                str.clear();
            }
        }
    }
    vector<int> v;
    for(int i=0 ; i < vs.size() ; i++ ){
    	v.push_back( s_to_i( vs[i] ) );
    }
    return v;
}

// v[0] := 学籍番号, v[1..v.size()-1] := 点数の記録
// score := 点数, pos := 位置
// frame := フレーム数, k := 0のとき1投目, 1のとき2投目
// (-スコア,学籍番号)を返す
P solve(vector<int>& v, int score, int pos, int frame, int k){
	P result;

	if( frame > 10 || pos >= v.size() ){
		result.second = v[0];
		result.first = -score;
		return result;
	}else{
		if( k == 0 ){ // 1投目
			if( v[pos] == 10 ){ // ストライクのとき	
				score += v[pos] + v[pos+1] + v[pos+2];
				return solve( v , score , pos+1 , frame+1 , 0 );
			}else{
				score += v[pos];
				return solve( v , score , pos+1 , frame , 1 );
			}
		}else{ // 2投目
			if( v[pos-1] + v[pos] == 10 ){ // スペアのとき
				score += v[pos] + v[pos+1];
				return solve( v , score , pos+1 , frame+1 , 0 );
			}else{
				score += v[pos];
				return solve( v , score , pos+1 , frame+1 , 0 );
			}
		}
	}

	return result;
}

int main(){
	int n;
	while( cin >> n , n ){
		cin.ignore();
		vector<P> ans;
		for(int i=0 ; i < n ; i++ ){
			string s;
			getline(cin,s);
			vector<int> v = split_words(s);
			P p = solve( v , 0 , 1 , 1 , 0 );
			ans.push_back(p);
		}

		sort( ans.begin() , ans.end() );
		for(int i=0 ; i < ans.size() ; i++ ){
			cout << ans[i].second << " " << -ans[i].first << endl;;
		}
	}
}

