        for(int i=0; i<m; i++){
            cin>>id[i];

            int turn = 1;
            int cnt = 0;
            int sum = 0;
            while(1){
                int s; cin>>s;
                score[i].eb(s);
                sum += s;
                cnt++;

                if(turn == 10){
                    if(sum == 10){
                        int s1, s2; cin>>s1>>s2;
