  string str;
  while(scanf("%d\n",&m),m) {
    for(int i=0;i<m;i++) {
      p temp;
      getline(cin,str);
      temp = score(str);
      V.push_back(temp);
    }
    sort(V.begin(),V.end(),compare);
    for(unsigned int i=0;i<V.size();i++) {
      cout << V[i].second << " " << V[i].first << endl;
    }
    V.clear();
  }
}

