struct game{
	int i;
	vector<int> point;
	game() : i(0){}
	game(int i) : i(i){}
	bool strike(){ return point[0] == 10;}
	bool spare(){ return point[0] + point[1] == 10;}
	bool fill(){ return strike() || sz(point) == 2;}
	int sum(){ return accumulate(point.begin(),point.end(),0); }
};

bool CMP(const Pii& l,const Pii &r){ return l.second == r.second ? l.first < r.first : l.second > r.second; }

