				if(sz(g) != 10 && g.back().fill())
					g.push_back(game(sz(v)));
			}

			int ans = 0;
			FOR(i,9){
				ans += g[i].sum();
				if(g[i].strike()){
					int c = g[i].i;
					ans += v[c+1] + v[c+2];
				} else if(g[i].spare()){
					ans += v[g[i].i+2];
				}
			}
			ans += g[9].sum();
			out.push_back(Pii(id,ans));
		}
		sort(out.begin(),out.end(),CMP);
		FOR(i,sz(out)){
