int point(vector<int>& v)
{
  int id = 0,pt = 0;
  for(int i=0; i<9; i++){
    if(v[id] == 10){
      pt += 10 + v[id+1] + v[id+2];
      id++;
    }else if(v[id] + v[id+1] == 10){
      pt += 10 + v[id+2];
      id += 2;
    }else{
      pt += v[id] + v[id+1];
      id += 2;
    }
  }
  while(id < v.size()){
    pt += v[id++];
  }
  return pt;
}
