int solve(vector<int> v){
  int t = 1, i = 1, res = 0;
  while(i < v.size()){
    if(t >= 10){
      res += v[i++];
    } else if(v[i] >= 10){
      res += v[i] + v[i + 1] + v[i + 2];
      ++i;
      ++t;
    } else if(v[i] + v[i + 1] >= 10){
      res += v[i] + v[i + 1] + v[i + 2];
      i += 2;
      ++t;
    } else{
      res += v[i] + v[i + 1];
      i += 2;
      ++t;
    }
  }
  return res;
}

