		memset(ScoreOfSet, 0, sizeof(ScoreOfSet));

		for (int i = 0; i < n; i++) {
			j = 0;
			getline(cin, str);
			ss << str;
			ss >> StuData[i].id;
			while (ss) {
				ss >> ScoreOfSet[j++];
			}
			j--;
			StuData[i].score = calculation(j);
			ss.clear();
			str = "";
		}
		sort(StuData, StuData + n, cmp);

		for (int i = 0; i < n; i++) {
			cout << StuData[i].id << " " << StuData[i].score << endl;
		}
	}

	return 0;
}

