typedef pair <int,int> P;
int main(){
  int n;
  while(cin>>n,n){
  vector <P> ans;
  while(n--){
    int ID,sum=0,cnt=0,ste[2]={},cnt2=0,pre=0,a,cnt3=0;
    cin>>ID;
    while(cnt<10){
      cin>>a;
      cnt3=!cnt3;
      cnt2++;
      while(ste[cnt3])sum+=a,ste[cnt3]--;
      if(cnt2==1&&a==10) ste[cnt3]++,ste[!cnt3]++,cnt2=2;
      else if(cnt2==2&&a+pre==10) ste[!cnt3]++;
      pre=a;
      if(cnt2==2) cnt++,cnt2=0,pre=0;
      sum+=a;
    }

    if(ste[!cnt3]) cin>>a;
    while(ste[!cnt3]--)sum+=a;
    if(ste[cnt3]) cin>>a;
    while(ste[cnt3]--)sum+=a;
    ans.push_back(P(sum,-ID));
  }

  sort(ans.begin(),ans.end());
  for(int i=(int)ans.size()-1;i>=0;i--) cout << -ans[i].second<<" "<<ans[i].first<<endl;
  }
  return 0;
}

