typedef pair<int,int> P; 
void split(vector<int>& vec,string line)
{
  stringstream ss;
  ss << line;
  for(int i=0;i<ss.str().size();i++)
    {
      string s;
      if((ss >>  s).fail())
	break;
      vec.push_back((atoi)(s.c_str()));
    }  
}

bool cmp(const P& a,const P& b)
{
  if(a.second != b.second)
    return a.second > b.second;
  return a.first < b.first;

}

int main()
{
  int n;
  while(cin >> n,n)
    {
      cin.ignore();
      vector<P> info;
      for(int i=0;i<n;i++)
	{
	  string line;
	  getline(cin,line);
	  vector<int> vec;
	  split(vec,line);

	  int total,cnt;
	  total = 0,cnt = 1;
	  for(int j=1;j<vec.size();cnt++)
	    {

	      if(cnt != 10)
		{
		  if(vec[j] == 10)
		    {
		      total += 10;
		      total += vec[j+1]+vec[j+2];
		      j++;
		    }
		  else 
		    {
		      if(vec[j] + vec[j+1] == 10)
			{
			  total += 10+vec[j+2];
			}
		      else 
			{
			  total += vec[j]+vec[j+1]; 

			}
		      j+=2;
		    }
		}
	      else
		{
		  if(vec[j]+vec[j+1] < 10)
		    {
		      total += vec[j]+vec[j+1];
		    }
		  else 
		    {
		      total += vec[j]+vec[j+1]+vec[j+2];
		    }

		  break;
		}

	    }
	  info.push_back(P(vec[0],total)); 

	}

      sort(info.begin(),info.end(),cmp);    
      for(int i=0;i<info.size();i++)
	cout << info[i].first << " " <<info[i].second << endl;

    }
  return 0;
}

