                    cin >> x;
                    fpin[11] = pins[11] = x;
                    strike[11] = x == 10;
                }
            }
            REP(j, 9) {
                term[j] = pins[j];
                if (pins[j] == 10) {
                    if (strike[j]) {
                        if (j+1 < 12) {
                            term[j] += pins[j+1];
                            if (strike[j+1] && j+2 < 12) {
                                term[j] += fpin[j+2];
                            }
