		vector<pair<int,int> >dat;
		for(int i=0;i<n;i++){
			int mem;
			cin>>mem;
			vector<int> scr;
			int a,b,c;
			for(int j=0;j<10;j++){
				cin>>a;
				scr.PB(a);
				if(a!=10||j==9){
					cin>>b;
					scr.PB(b);
				}else continue;
				if(j==9&&(a+b==10||a+b==20)){
					cin>>c;
					scr.PB(c);
				}else continue;
			}
			int ans=0,k=0;
