		r+=*a;
		if(*a+s==10)
		{
			if(f)r+=a[1];
			else r+=a[1]+a[2];
			f=0,s=0;
			++c;
		}
		else
		{
			if(f)f=0,++c,s=0;
			else f=1,s=*a;
		}
		++a;
	}
	s=*a+a[1];
	if(s>9)s+=a[2];
	return r+s;
}
