      getline(cin, s);
      int N = split(s);

      int res = 0, flame = 1;
      for(int i = 0 ; flame <= 10 ; ){
	if(flame == 10){
	  if(rec[i] + rec[i+1] == 10) res += 10 + rec[i+2];
	  else if(rec[i] == 10) res += 10 + rec[i+1]+rec[i+2];
	  else res += rec[i] + rec[i+1];
	  flame++;
	}
	else if(rec[i] + rec[i+1] == 10){
	  res += 10 + rec[i+2];
	  i+=2;
	  flame++;
	}
	else if(rec[i] == 10){
	  res += 10 + rec[i+1] + rec[i+2];
	  i++;
	  flame++;
	}
	else{
	  res += rec[i] + rec[i+1];
	  i+=2;
	  flame++;
	}
      }
      data.push_back(P(res, ID));
    }
    sort(data.begin(), data.end(), compare);
    for(int i = 0 ; i < n ; i++){
