			frames[10][1] = 0;
			cin >> frames[11][0];
		}
		else {
			cin >> frames[10][1];
		}
	}
	else if (frames[9][0] + frames[9][1] == 10) {
		cin >> frames[10][0];
		frames[10][1] = 0;
	}

	int result = 0;
