using namespace std;

int calcscore()
{
	int score=0,strike=0,spare=0,pin[2];

	for(int i=1;i<=9;i++){
		for(int j=0;j<2;j++){
			if(j==1 && pin[0]==10)	continue;
			cin>>pin[j];
			if(strike>0)	score+=(strike%10)*pin[j],strike/=10;
			if(spare>0)		score+=pin[j],spare--;
			score+=pin[j];
			if(j==1)	pin[j]+=pin[j-1];
			if(pin[j]==10){
				if(j==0)	strike+=11;
				if(j==1)	spare++;
			}
		}
	}

	for(int i=0;i<2;i++){
		cin>>pin[i];
		if(strike>0)	score+=(strike%10)*pin[i],strike/=10;
		if(spare>0)		score+=pin[i],spare--;
		score+=pin[i];
	}
	if(pin[0]+pin[1]>=10){
		cin>>pin[0];
		score+=pin[0];
	}

	return score;
}

