#include <cstdio>
#include <algorithm>
using namespace std;

int calc(int a[21]) {
  bool spare[21] = {false};
  bool strike[21] = {false};
  int s = 0, p = 0;
  for (int i=0; i<10; i++) {
    s += a[p];
    if (p>0 && (spare[p-1] || strike[p-1])) {
      s += a[p];
    }
    if (p>1 && strike[p-2]) {
      s += a[p];
    }
    if (a[p] == 10) {
      strike[p] = true;
      p++;
      continue;
    }
    s += a[p+1];
    if (p>0 && strike[p-1]) {
      s += a[p+1];
    }
    if (a[p] + a[p+1] == 10) {
      spare[p+1] = true;
    }
    p += 2;
  }
  if (spare[p-1]) {
    s += a[p];
  }
  if (strike[p-1]) {
    s += a[p];
    if (strike[p-2]) {
      s += a[p];
    }
    s += a[p+1];
  }
  return s;
}


