int calc(std::vector<int> const& pts) {
  std::vector<std::vector<int>> frame(10);
  {
    size_t i = 0;
    for (int p: pts) {
      frame[i].push_back(p);
      if (i < 9) {
        if (frame[i].size() == 2) {
          ++i;
        } else if (std::accumulate(frame[i].begin(), frame[i].end(), 0) == 10) {
          ++i;
        }
      }
    }
  }
  std::vector<int> framescore(10);
  for (size_t i = 0; i < 9; ++i) {
    framescore[i] = std::accumulate(frame[i].begin(), frame[i].end(), 0);
    if (framescore[i] == 10) {
      if (frame[i].size() == 1) {
        // strike
        framescore[i] += frame[i+1][0];
        if (frame[i+1].size() > 1) {
          framescore[i] += frame[i+1][1];
        } else {
          framescore[i] += frame[i+2][0];
        }
      } else {
        assert(frame[i].size() == 2);
        // spare
        framescore[i] += frame[i+1][0];
      }
    }
  }
  framescore[9] = std::accumulate(frame[9].begin(), frame[9].end(), 0);
  return std::accumulate(framescore.begin(), framescore.end(), 0);
}

