using namespace std;

void solve(vector<vector<int> > v){
    map<int,int> point2num;
    vector<int> points;
    for(int i=0;i<v.size();i++){
        int num = v[i][0];
        vector<int> frame(11);
        frame[0] = 0;
        int f=1;int j=1;
        while(true){
            if(f==11)break;
            int x = v[i][j];
            if(x==10){
                frame[f]=10+v[i][j+1]+v[i][j+2];
                f++;j++;
                continue;
            }
            j++;
            x = x+v[i][j];
            if(x==10){
                frame[f]=10+v[i][j+1];
                f++;j++;
                continue;
            }
            frame[f] = x;
            f++;j++;
            continue;
        }
        int p=0;
        for(int j=0;j<frame.size();j++){
            p+=frame[j];
        }
        points.push_back(p);
        point2num.insert(map<int,int>::value_type(p,num));
    }
    sort(points.begin(),points.end(),greater<int>());
    for(int i=0;i<points.size();i++){
        cout << point2num[points[i]] << " " << points[i] << endl;
    }
}

