int main() {

    ll m;
    while (cin >> m, m) {

        vector<ll> id(m);
        vector<ll> score(m, 0);

        REP(i, m) {
            cin >> id[i];
            ll sl = 0, r = 0;
            ll b = 0;
            vector<ll> pins(12, 0);
            vector<ll> fpin(12, 0);
            vector<bool> strike(12, false);
            vector<ll> term(12, 0);
            while (sl < 10) {
                ll x; cin >> x;
                r += x;
                if (b == 0) fpin[sl] = x;
                b++;
                if (r == 10 || b == 2) {
                    pins[sl] = r;
                    strike[sl] = r == 10 && b == 1;
                    b = 0;
                    r = 0;
                    sl++;
                }
            }
            if (pins[9] == 10) {
                ll x; cin >> x;
                fpin[10] = pins[10] = x;
                if (x == 10) {
                    strike[10] = true;
                } else {
                    cin >> x;
                    pins[10] += x;
                }
                if (strike[10] && strike[9]) {
                    cin >> x;
                    fpin[11] = pins[11] = x;
                    strike[11] = x == 10;
                }
            }
            REP(j, 9) {
                term[j] = pins[j];
                if (pins[j] == 10) {
                    if (strike[j]) {
                        if (j+1 < 12) {
                            term[j] += pins[j+1];
                            if (strike[j+1] && j+2 < 12) {
                                term[j] += fpin[j+2];
                            }
                        }
                    } else {
                        if (j+1 < 12) term[j] += fpin[j+1];
                    }
                }
            }
            term[9] = pins[9] + pins[10] + pins[11];
            score[i] = accumulate(ALL_OF(term), 0LL);
        }

        vector<ll> rank(m, 0);
        iota(ALL_OF(rank), 0LL);
        sort(ALL_OF(rank), [&](ll i, ll j) -> bool {
            if (score[i] != score[j]) {
                return score[i] > score[j];
            } else {
                return id[i] < id[j];
            }
        });

        REP(_i, m) {
            ll i = rank[_i];
            cout << id[i] << " " << score[i] << "\n";
        }

    }

}

