void process_prevs(vector<int>& scores, vector<pair<int, int> >& prevs, int a) {
  rep (i in prevs.size()) {
    if (prevs[i].second) {
      scores[prevs[i].first] += a;
      prevs[i].second--;
    }
  }
}

bool cmp(pair<int, int> a, pair<int, int> b) {
  return a.second > b.second;
}

int main() {
  int N;
  while (cin >> N && N) {
    vector<pair<int, int> > players(N);
    rep (i in N) {
      int id; cin >> id;
      vector<pair<int, int> > prevs;
      vector<int> scores(10);
      int j = 0;
      while (j < 9) {
        int a; cin >> a;
        process_prevs(scores, prevs, a);
        if (a == 10) {
          prevs.pb(mp(j, 2));
          scores[j] = a;
          j++;
          continue;
        }
        int b; cin >> b;
        process_prevs(scores, prevs, b);
        if (a+b == 10) {
          prevs.pb(mp(j, 1));
          scores[j] = a+b;
          j++;
          continue;
        }
        scores[j] = a+b;
        j++;
      }
      int a, b; cin >> a >> b;
      process_prevs(scores, prevs, a);
      process_prevs(scores, prevs, b);
      int three = false;
      if (a == 10) three = true;
      if (a+b == 10) three = true;
      if (three) {
        int c; cin >> c;
        scores[9] = a+b+c;
      } else {
        scores[9] = a+b;
      }
      int score = 0;
      rep (e in scores) {
        score += e;
      }
      players[i] = mp(id, score);
    }
    sort(all(players), cmp);
    rep (e in players) {
      cout << e.first << " " << e.second << endl;
    }
  }
}

