typedef struct {
	int id;
	int score;
} man_t;

int compare(const void* x,const void* y) {
	man_t* a;
	man_t* b;
	a=(man_t*)x;
	b=(man_t*)y;
	if(a->score<b->score)return 1;
	if(a->score>b->score)return -1;
	if(a->id>b->id)return 1;
	if(a->id<b->id)return -1;
	return 0;
}

