int calc() {
  int point = 0;
  int i;
  int pin[21], top, frame;

  // ÇÝÝ
  for (i = 0; getchar() != '\n'; i++) {
    scanf("%d", pin + i);
    //printf("%d ", pin[i]);
  }
  //puts("");
  top = i;

  for (i = frame = 0; frame < 9; frame++) {
    if (pin[i] == 10) {
      point += pin[i] + pin[i + 1] + pin[i + 2];
      i++;
    } else {
      if (pin[i] + pin[i + 1] == 10) {
	point += pin[i] + pin[i + 1] + pin[i + 2];
      } else {
	point += pin[i] + pin[i + 1];
      }
      i += 2;
    }
  }

  while (i < top) { point += pin[i++]; }

  return point;
}

