typedef struct {
    int no;
    int score;
} SCORE;

SCORE score[40];

void game(SCORE *score)
{
    int frame;
    int i;
    int bonus1, bonus2;
    int pin1, pin2;

    scanf("%d", &score->no);

    score->score = 0;
    bonus1 = bonus2 = 0;

    for (frame = 1; frame <= 9; frame++){
        scanf("%d", &pin1);
        score->score += (pin1 * (bonus1 + 1));

//        printf("[%d:%d]\n", frame, score->score);

        bonus1 = bonus2;
        bonus2 = 0;
        if (pin1 == 10){
            bonus1++;
            bonus2++;
            continue;
        }



        scanf("%d", &pin2);
        score->score += (pin2 * (bonus1 + 1));

//        printf("[%d:%d]\n", frame, score->score);

        bonus1 = bonus2;
        bonus2 = 0;
        if (pin1 + pin2 == 10){
            bonus1++;
        }
    }

    scanf("%d", &pin1);
    score->score += (pin1 * (bonus1 + 1));

//            printf("[10:%d]\n", score->score);

    bonus1 = bonus2;


    scanf("%d", &pin2);
    score->score += (pin2 * (bonus1 + 1));

//            printf("[10:%d]\n", score->score);

    if (pin1 == 10 || (pin1 + pin2) == 10){
        scanf("%d", &pin1);
        score->score += pin1;
//        printf("[10:%d]\n", score->score);
    }

}


void sort_game(int n)
{
    SCORE tmp;
    int i, j;

    for (i = 0; i < n; i++){
        for (j = 0; j < n - 1; j++){
            if (score[j].score < score[j + 1].score){
                tmp = score[j];
                score[j] = score[j + 1];
                score[j + 1] = tmp;
            }
        }
    }
}

int main(void)
{
    int m;
    int i;

    while (1){
        scanf("%d", &m);
        if (m == 0){
            break;
        }

        for (i = 0; i < m; i++){
            game(&score[i]);
       }

        sort_game(m);

        for (i = 0; i < m; i++){
            printf("%d %d\n", score[i].no, score[i].score);
        }
    }

    return (0);
}

