class BowringScore{
    private var mIsNextSpare = 0
    private var mIsNextStrike = 0
    var currentScore:Int = 0
        private set
    private var mFirst = true
    private var flame = 0
    private var mRest:Int = 10
    fun throwBall(num:Int):Unit {
        if (mFirst) ++flame
        if (flame <= 10) {
            currentScore += num * (1 + mIsNextSpare + mIsNextStrike)
            mIsNextSpare = mIsNextStrike
            mIsNextStrike = 0
            if (mFirst) {
                if (num == 10) {
                    mIsNextStrike = 1
                } else {
                    mRest -= num
                    mFirst = false
                }
            } else {
                if (num == mRest) {
                    mIsNextSpare += 1
                }
                mRest = 10
                mFirst = true
            }
        }else{
            currentScore += num * (mIsNextSpare + mIsNextStrike)
            mIsNextSpare = mIsNextStrike
            mIsNextStrike = 0
        }
    }
}

